class Solution:
    def deleteAndEarn(self, nums):
        from collections import Counter
        count = Counter(nums)
        max_num = max(nums)
        dp = [0] * (max_num + 1)
        dp[1] = count.get(1, 0) * 1
        for i in range(2, max_num + 1):
            dp[i] = max(dp[i - 1], dp[i - 2] + count.get(i, 0) * i)
        return dp[max_num]
