class Solution:
    def shortestCompletingWord(self, licensePlate, words):
        import collections
        plate_count = collections.Counter(c.lower() for c in licensePlate if c.isalpha())
        res = None
        for word in words:
            word_count = collections.Counter(word)
            if all(word_count[c] >= plate_count[c] for c in plate_count):
                if res is None or len(word) < len(res):
                    res = word
        return res
