class Solution:
    def maxChunksToSorted(self, arr):
        n = len(arr)
        right_min = [0] * n
        right_min[-1] = arr[-1]
        for i in range(n-2, -1, -1):
            right_min[i] = min(arr[i], right_min[i+1])

        chunks = 0
        left_max = float('-inf')
        for i in range(n-1):
            left_max = max(left_max, arr[i])
            if left_max <= right_min[i+1]:
                chunks += 1
        return chunks + 1
