class Solution:
    def rotatedDigits(self, n):
        good = {'2', '5', '6', '9'}
        valid = {'0','1','2','5','6','8','9'}
        count = 0
        for num in range(1, n+1):
            s = str(num)
            if all(c in valid for c in s) and any(c in good for c in s):
                count += 1
        return count
