class Solution:
    def escapeGhosts(self, ghosts, target):
        player_dist = abs(target[0]) + abs(target[1])
        for gx, gy in ghosts:
            ghost_dist = abs(target[0] - gx) + abs(target[1] - gy)
            if ghost_dist <= player_dist:
                return False
        return True
