class Solution:
    def preimageSizeFZF(self, K):
        def trailingZeroes(n):
            count = 0
            while n:
                n //= 5
                count += n
            return count

        left, right = 0, 5 * K
        while left <= right:
            mid = (left + right) // 2
            z = trailingZeroes(mid)
            if z < K:
                left = mid + 1
            else:
                right = mid - 1

        return 5 if trailingZeroes(left) == K else 0
