class Solution:
    def hitBricks(self, grid, hits):
        import copy
        m, n = len(grid), len(grid[0])
        g = copy.deepcopy(grid)
        for i, j in hits:
            g[i][j] = 0

        def index(x, y):
            return x * n + y

        parent = [i for i in range(m * n + 1)]
        size = [1] * (m * n + 1)

        def find(x):
            while parent[x] != x:
                parent[x] = parent[parent[x]]
                x = parent[x]
            return x

        def union(x, y):
            xroot, yroot = find(x), find(y)
            if xroot == yroot:
                return
            parent[yroot] = xroot
            size[xroot] += size[yroot]

        for j in range(n):
            if g[0][j]:
                union(index(0, j), m * n)

        for i in range(1, m):
            for j in range(n):
                if g[i][j]:
                    if g[i-1][j]:
                        union(index(i, j), index(i-1, j))
                    if j > 0 and g[i][j-1]:
                        union(index(i, j), index(i, j-1))

        res = []
        for i, j in reversed(hits):
            if grid[i][j] == 0:
                res.append(0)
                continue
            pre = size[find(m * n)]
            g[i][j] = 1
            for x, y in [(i-1,j),(i+1,j),(i,j-1),(i,j+1)]:
                if 0 <= x < m and 0 <= y < n and g[x][y]:
                    union(index(i,j), index(x,y))
            if i == 0:
                union(index(i,j), m * n)
            cur = size[find(m * n)]
            res.append(max(0, cur - pre - 1))
        return res[::-1]
