class Solution:
    def splitArraySameAverage(self, A):
        N, S = len(A), sum(A)
        dp = [set() for _ in range(N//2 + 1)]
        dp[0].add(0)
        for num in A:
            for i in range(len(dp) - 2, -1, -1):
                for prev_sum in dp[i]:
                    dp[i+1].add(prev_sum + num)
        for i in range(1, N//2 + 1):
            if S * i % N == 0 and S * i // N in dp[i]:
                return True
        return False
