class Solution:
    def numberOfLines(self, widths, s):
        lines = 1
        width_sum = 0
        for c in s:
            w = widths[ord(c) - ord('a')]
            if width_sum + w > 100:
                lines += 1
                width_sum = w
            else:
                width_sum += w
        return [lines, width_sum]
