class Solution:
    def expressiveWords(self, s, words):
        def valid(word):
            i = j = 0
            while i < len(s) and j < len(word):
                if s[i] != word[j]:
                    return False
                si = i
                while i < len(s) and s[i] == s[si]:
                    i += 1
                wj = j
                while j < len(word) and word[j] == word[wj]:
                    j += 1
                if (i - si) < (j - wj) or ((i - si) < 3 and (i - si) != (j - wj)):
                    return False
            return i == len(s) and j == len(word)
        return sum(valid(word) for word in words)
