class Solution:
    def xorGame(self, nums):
        from functools import reduce
        from operator import xor

        xor_all = reduce(xor, nums)
        # 如果 XOR 已經為 0，Alice 先手直接贏
        if xor_all == 0:
            return True
        # 如果陣列長度為偶數，Alice 也可以保證勝利
        if len(nums) % 2 == 0:
            return True
        # 否則，Alice 先手輸
        return False
