class Solution(object):
    def largestSumOfAverages(self, nums, k):
        """
        :type nums: List[int]
        :type k: int
        :rtype: float
        """
        n = len(nums)
        prefix = [0] * (n + 1)
        for i in range(n):
            prefix[i+1] = prefix[i] + nums[i]

        dp = [[0] * (k + 1) for _ in range(n + 1)]

        for i in range(1, n + 1):
            dp[i][1] = float(prefix[i]) / i

        for kk in range(2, k + 1):
            for i in range(kk, n + 1):
                for j in range(kk - 1, i):
                    dp[i][kk] = max(dp[i][kk], dp[j][kk-1] + float(prefix[i]-prefix[j])/(i-j))

        return dp[n][k]
