class Solution(object):
    def ambiguousCoordinates(self, S):
        def generate(s):
            res = []
            if s == "0":
                res.append("0")
                return res
            if s[0] != "0":
                res.append(s)
            for i in range(1, len(s)):
                left, right = s[:i], s[i:]
                if (left[0] != "0" or left == "0") and (right[-1] != "0"):
                    res.append(left + "." + right)
            return res

        S = S[1:-1]
        n = len(S)
        ans = []
        for i in range(1, n):
            left, right = S[:i], S[i:]
            lefts = generate(left)
            rights = generate(right)
            for l in lefts:
                for r in rights:
                    ans.append("({}, {})".format(l, r))
        return ans
