class Solution:
    def toGoatLatin(self, sentence):
        vowels = set('aeiouAEIOU')
        words = sentence.split()
        res = []
        for i, word in enumerate(words, 1):
            if word[0] in vowels:
                word = word + 'ma'
            else:
                word = word[1:] + word[0] + 'ma'
            word += 'a' * i
            res.append(word)
        return ' '.join(res)
