class Solution:
    def numFriendRequests(self, ages):
        from collections import Counter
        count = Counter(ages)
        res = 0
        for a in count:
            for b in count:
                if b <= 0.5 * a + 7:
                    continue
                if b > a:
                    continue
                res += count[a] * count[b] if a != b else count[a] * (count[a] - 1)
        return res
