class Solution(object):
    def largeGroupPositions(self, s):
        res = []
        i = 0
        n = len(s)
        while i < n:
            start = i
            while i + 1 < n and s[i] == s[i + 1]:
                i += 1
            if i - start + 1 >= 3:
                res.append([start, i])
            i += 1
        return res
