class Solution(object):
    def maskPII(self, S):
        S = S.strip()
        if '@' in S:
            name, domain = S.split('@')
            return name[0].lower() + '*****' + name[-1].lower() + '@' + domain.lower()
        else:
            digits = [c for c in S if c.isdigit()]
            local = digits[-4:]
            country_len = len(digits) - 10
            if country_len == 0:
                return '***-***-' + ''.join(local)
            else:
                return '+' + '*'*country_len + '-***-***-' + ''.join(local)
