class Solution:
    def canVisitAllRooms(self, rooms):
        visited = [False] * len(rooms)
        stack = [0]
        visited[0] = True

        while stack:
            room = stack.pop()
            for key in rooms[room]:
                if not visited[key]:
                    visited[key] = True
                    stack.append(key)

        return all(visited)
