class Solution(object):
    def splitIntoFibonacci(self, num):
        res = []

        def backtrack(index):
            if index == len(num) and len(res) >= 3:
                return True
            curr_num = 0
            for i in range(index, len(num)):
                if i > index and num[index] == '0':
                    break
                curr_num = curr_num * 10 + int(num[i])
                if curr_num > 2**31 - 1:
                    break
                if len(res) >= 2 and curr_num != res[-1] + res[-2]:
                    if curr_num > res[-1] + res[-2]:
                        break
                    else:
                        continue
                res.append(curr_num)
                if backtrack(i + 1):
                    return True
                res.pop()
            return False

        if backtrack(0):
            return res
        return []
