class Solution(object):
    def loudAndRich(self, richer, quiet):
        """
        :type richer: List[List[int]]
        :type quiet: List[int]
        :rtype: List[int]
        """
        from collections import defaultdict

        n = len(quiet)
        graph = defaultdict(list)
        for u, v in richer:
            graph[v].append(u)

        ans = [-1] * n

        def dfs(node):
            if ans[node] != -1:
                return ans[node]
            min_person = node
            for nei in graph[node]:
                cand = dfs(nei)
                if quiet[cand] < quiet[min_person]:
                    min_person = cand
            ans[node] = min_person
            return min_person

        for i in range(n):
            dfs(i)

        return ans
