import math

class Solution(object):
    def primePalindrome(self, N):
        if 8 <= N <= 11:
            return 11

        for length in range(1, 6):
            for half in range(10**(length-1), 10**length):
                s = str(half)
                pal = int(s + s[-2::-1])
                if pal >= N and self.isPrime(pal):
                    return pal

        for pal in [100030001, 100050001, 100080001, 100110001]:
            if pal >= N and self.isPrime(pal):
                return pal

    def isPrime(self, n):
        if n < 2:
            return False
        if n == 2:
            return True
        if n % 2 == 0:
            return False
        for i in range(3, int(math.sqrt(n)) + 1, 2):
            if n % i == 0:
                return False
        return True
