class Solution(object):
    def isScramble(self, s1, s2):
        memo = {}

        def dfs(x, y):
            if (x, y) in memo:
                return memo[(x, y)]
            if x == y:
                memo[(x, y)] = True
                return True
            if sorted(x) != sorted(y):
                memo[(x, y)] = False
                return False

            n = len(x)
            for i in range(1, n):
                if dfs(x[:i], y[:i]) and dfs(x[i:], y[i:]):
                    memo[(x, y)] = True
                    return True
                if dfs(x[:i], y[-i:]) and dfs(x[i:], y[:-i]):
                    memo[(x, y)] = True
                    return True

            memo[(x, y)] = False
            return False

        return dfs(s1, s2)
