class Solution(object):
    def advantageCount(self, A, B):
        import collections
        n = len(A)
        A.sort()
        B_sorted = sorted([(b, i) for i, b in enumerate(B)], reverse=True)
        res = [0] * n
        left, right = 0, n - 1

        for b, i in B_sorted:
            if A[right] > b:
                res[i] = A[right]
                right -= 1
            else:
                res[i] = A[left]
                left += 1

        return res
