class Solution(object):
    def robotSim(self, commands, obstacles):
        directions = [(0,1), (1,0), (0,-1), (-1,0)]
        x = y = di = 0
        obstacleSet = set(map(tuple, obstacles))
        maxDist = 0

        for cmd in commands:
            if cmd == -2:  # left turn
                di = (di + 3) % 4
            elif cmd == -1:  # right turn
                di = (di + 1) % 4
            else:
                dx, dy = directions[di]
                for _ in range(cmd):
                    if (x + dx, y + dy) not in obstacleSet:
                        x += dx
                        y += dy
                        maxDist = max(maxDist, x*x + y*y)
                    else:
                        break
        return maxDist
