class Solution(object):
    def minEatingSpeed(self, piles, H):
        import math
        left, right = 1, max(piles)
        while left < right:
            mid = (left + right) // 2
            hours = sum(math.ceil(pile / float(mid)) for pile in piles)
            if hours <= H:
                right = mid
            else:
                left = mid + 1
        return left
