class Solution(object):
    def fairCandySwap(self, A, B):
        """
        :type A: List[int]
        :type B: List[int]
        :rtype: List[int]
        """
        sumA, sumB = sum(A), sum(B)
        setB = set(B)
        delta = (sumA - sumB) // 2
        for a in A:
            if a - delta in setB:
                return [a, a - delta]
