class Solution(object):
    def numSpecialEquivGroups(self, A):
        """
        :type A: List[str]
        :rtype: int
        """
        unique = set()
        for word in A:
            # 將偶數位和奇數位分開排序，作為字串特徵
            key = (tuple(sorted(word[::2])), tuple(sorted(word[1::2])))
            unique.add(key)
        return len(unique)
