class Solution(object):
    def subsetsWithDup(self, nums):
        nums.sort()
        res = [[]]
        for num in nums:
            new_subsets = []
            for subset in res:
                new_subsets.append(subset + [num])
            res += new_subsets
        unique_res = []
        seen = set()
        for subset in res:
            t = tuple(subset)
            if t not in seen:
                seen.add(t)
                unique_res.append(subset)
        return unique_res
