class RLEIterator(object):
    def __init__(self, A):
        self.A = A
        self.index = 0

    def next(self, n):
        while self.index < len(self.A) and n > self.A[self.index]:
            n -= self.A[self.index]
            self.index += 2
        if self.index >= len(self.A):
            return -1
        self.A[self.index] -= n
        return self.A[self.index + 1]
