class Solution(object):
    def atMostNGivenDigitSet(self, digits, n):
        s = str(n)
        k = len(s)
        d = len(digits)
        ans = sum(d**i for i in range(1, k))
        for i in range(k):
            smaller = sum(int(d) < int(s[i]) for d in digits)
            ans += smaller * (d ** (k - i - 1))
            if s[i] not in digits:
                return ans
        return ans + 1
