class Solution(object):
    def numPermsDISequence(self, S):
        MOD = 10 ** 9 + 7
        n = len(S)
        dp = [1] * (n+1)
        for i, ch in enumerate(S):
            ndp = [0] * (n+1)
            if ch == 'D':
                total = 0
                for j in reversed(range(n-i)):
                    total = (total + dp[j+1]) % MOD
                    ndp[j] = total
            else:
                total = 0
                for j in range(n-i):
                    total = (total + dp[j]) % MOD
                    ndp[j] = total
            dp = ndp
        return sum(dp[:n]) % MOD
