class Solution(object):
    def smallestRangeII(self, A, K):
        A.sort()
        res = A[-1] - A[0]
        for i in range(len(A)-1):
            high = max(A[-1]-K, A[i]+K)
            low = min(A[0]+K, A[i+1]-K)
            res = min(res, high - low)
        return res
