class TopVotedCandidate(object):

    def __init__(self, persons, times):
        from collections import defaultdict
        self.times = times
        self.leaders = []
        count = defaultdict(int)
        leader = -1
        max_count = 0
        for p in persons:
            count[p] += 1
            if count[p] >= max_count:
                leader = p
                max_count = count[p]
            self.leaders.append(leader)

    def q(self, t):
        left, right = 0, len(self.times)-1
        while left <= right:
            mid = (left + right) // 2
            if self.times[mid] <= t:
                left = mid + 1
            else:
                right = mid - 1
        return self.leaders[right]
