class Solution(object):
    def sortArrayByParityII(self, A):
        n = len(A)
        res = [0] * n
        even_index, odd_index = 0, 1
        for num in A:
            if num % 2 == 0:
                res[even_index] = num
                even_index += 2
            else:
                res[odd_index] = num
                odd_index += 2
        return res
