cor_exp:
# 在方法中使用屬性
class Circle:
    def __init__(self, radius):
        self.radius = radius

    def area(self):
        return 3.14 * (self.radius ** 2)

c = Circle(5)
print('半徑：', c.radius)
print('面積：', c.area())

mis_exp:
# 錯誤：未使用 self 設定屬性
class Dog:
    def __init__(self, name):
        name = name  # 錯誤：應為 self.name = name

my_dog = Dog('Lucky')
print(my_dog.name)  # AttributeError：找不到屬性 name

# 錯誤：初始化屬性時缺少參數
class Car:
    def __init__(self, brand):
        self.brand = brand

my_car = Car()  # TypeError：未傳入 brand 參數

# 錯誤：動態新增不存在的屬性（拼錯）
class Student:
    def __init__(self, name):
        self.name = name

s = Student('Bob')
s.nmae = 'Tom'  # 拼錯屬性名，不會修改原本的 name
