cor_exp:
# 定義方法並呼叫
class Student:
    def __init__(self, name, score):
        self.name = name
        self.score = score

    def show_info(self):
        print(f'{self.name} 的成績是 {self.score}')

s1 = Student('Alice', 92)
s1.show_info()  # Alice 的成績是 92

# 方法中修改屬性
class BankAccount:
    def __init__(self, owner, balance):
        self.owner = owner
        self.balance = balance

    def deposit(self, amount):
        self.balance += amount
        print(f'存款 {amount} 元，餘額為 {self.balance} 元')

    def withdraw(self, amount):
        if amount > self.balance:
            print('餘額不足！')
        else:
            self.balance -= amount
            print(f'提款 {amount} 元，剩餘 {self.balance} 元')

acc = BankAccount('Tom', 1000)
acc.deposit(500)
acc.withdraw(300)

# 方法回傳值
class Circle:
    def __init__(self, radius):
        self.radius = radius

    def area(self):
        return 3.14 * (self.radius ** 2)

c = Circle(5)
print('圓面積：', c.area())

mis_exp:
# 錯誤：呼叫未定義的方法
class Car:
    def start(self):
        print('發動引擎')

my_car = Car()
my_car.stop()  # AttributeError：類別中沒有 stop 方法

# 錯誤：誤將方法當變數使用
class Student:
    def say_hi(self):
        print('Hi!')

s = Student()
print(s.say_hi)  # 顯示方法記憶體位址，需加括號呼叫

