cor_exp:
# 基本繼承
class Animal:
    def speak(self):
        print('動物發出聲音')

class Dog(Animal):
    def bark(self):
        print('汪汪！')

my_dog = Dog()
my_dog.speak()  # 繼承父類方法
my_dog.bark()   # 子類自訂方法

# 使用 super() 呼叫父類建構子
class Person:
    def __init__(self, name):
        self.name = name

class Student(Person):
    def __init__(self, name, score):
        super().__init__(name)  # 呼叫父類建構子
        self.score = score

s = Student('Alice', 90)
print(s.name, s.score)

# 多型（方法覆寫）
class Animal:
    def make_sound(self):
        print('動物發出聲音')

class Cat(Animal):
    def make_sound(self):
        print('喵～')

class Dog(Animal):
    def make_sound(self):
        print('汪汪！')

for a in [Cat(), Dog(), Animal()]:
    a.make_sound()

mis_exp:
# 錯誤：未正確繼承父類
class Dog:
    def bark(self):
        print('汪汪')

class Puppy:
    pass

p = Puppy()
p.bark()  # AttributeError：Puppy 沒有繼承 Dog

# 錯誤：忘記使用 super()
class Person:
    def __init__(self, name):
        self.name = name

class Student(Person):
    def __init__(self, name, score):
        self.score = score  # 忘記初始化 name

s = Student('Amy', 85)
print(s.name)  # AttributeError：未建立 name 屬性

# 錯誤：覆寫方法但拼錯名稱
class Animal:
    def speak(self):
        print('動物聲音')

class Cat(Animal):
    def speek(self):  # 拼錯，未成功覆寫
        print('喵')

c = Cat()
c.speak()  # 仍執行父類方法

