cor_exp:
for i in range(5):
    if i == 3:
        break
    print(i)  # 輸出 0,1,2

for i in range(5):
    if i == 2:
        continue
    print(i)  # 輸出 0,1,3,4

i = 0
while i < 5:
    i += 1
    if i == 4:
        break
    print(i)  # 輸出 1,2,3

mis_exp:
for i in range(5):
    if i == 3
        break  # 錯誤：缺少冒號

