cor_exp:
# clear(): 清空字典內容，但字典仍存在
scores.clear()
print(scores)  # {}

# copy(): 複製字典
students = {'Tom': 18, 'Amy': 19}
students_copy = students.copy()
students_copy['Tom'] = 20
print(students)       # {'Tom': 18, 'Amy': 19}
print(students_copy)  # {'Tom': 20, 'Amy': 19}

# pop(): 移除元素並回傳值
grades = {'數學': 80, '英文': 90, '國文': 85}
removed = grades.pop('英文')
print(removed)  # 90
print(grades)   # {'數學': 80, '國文': 85}

mis_exp:
grades = {'數學': 80, '英文': 90}
grades.pop('歷史')  # KeyError
# 錯誤：pop() 移除不存在的 key 會報錯
