def is_prime(n):
    if n <= 1:
        return False
    for i in range(2, int(n**0.5) + 1):
        if n % i == 0:
            return False
    return True

# 測試
print(is_prime(7))   # True
print(is_prime(12))  # False
print(is_prime(1))   # False

def calc_avg(*scores):
    total = 0
    count = 0
    for s in scores:
        total += s
        count += 1
    return round(total / count, 2)

# 測試
print(calc_avg(80, 90, 100))      # 90.0
print(calc_avg(70, 75, 85, 95))   # 81.25
