try:
    a, b = map(int, input('請輸入兩個整數（以空格分隔）: ').split())
    print('結果為:', a / b)
except (ValueError, ZeroDivisionError):
    print('輸入錯誤或除數為 0')

try:
    with open('data.txt', 'r', encoding='utf-8') as f:
        lines = f.readlines()
        print('檔案共有', len(lines), '行')
except FileNotFoundError:
    print('找不到檔案！')
