cor_exp:
# 多重 except 處理不同錯誤
try:
    x = int(input('請輸入整數 x: '))
    y = int(input('請輸入整數 y: '))
    print('結果:', x / y)
except ValueError:
    print('輸入錯誤：請輸入整數數值')
except ZeroDivisionError:
    print('錯誤：除數不可為 0')
except Exception as e:
    print('其他錯誤：', e)

# 若輸入字母會觸發 ValueError，若輸入 y = 0 會觸發 ZeroDivisionError

mis_exp:
# 錯誤：未分開處理錯誤類型
try:
    num = int(input('輸入整數: '))
    print(10 / num)
except:
    print('有錯誤發生')  # 雖可執行，但無法分辨錯誤類型

