cor_exp:
# 基本 raise 使用
x = int(input('請輸入年齡: '))
if x < 0:
    raise ValueError('年齡不能為負數')
else:
    print('你的年齡是', x)

# 搭配 try / except 捕捉自訂例外
try:
    score = int(input('請輸入分數: '))
    if score > 100:
        raise ValueError('分數不得超過 100')
    elif score < 0:
        raise ValueError('分數不得小於 0')
    print('分數有效')
except ValueError as e:
    print('輸入錯誤：', e)

